import React from 'react';
import { Link } from 'react-router-dom';

const UserCard = ({ user }) => {
  return (
    <div className="relative p-[2px] rounded-xl overflow-hidden group">
      {/* Animated border background */}
      <div className="absolute inset-0 bg-gradient-to-r from-pink-500 via-purple-500 to-blue-500 animate-hueRotate opacity-75 group-hover:opacity-100 transition duration-300"></div>
      
      {/* Card Content */}
      <div className="relative bg-white rounded-xl shadow-md p-6 hover:shadow-xl transition-shadow duration-300">
        <div className="flex items-center mb-4">
          <div className="w-12 h-12 bg-blue-500 rounded-full flex items-center justify-center text-white font-bold text-lg">
            {user.name.charAt(0).toUpperCase()}
          </div>
          <div className="ml-4">
            <h3 className="text-lg font-semibold text-gray-800">{user.name}</h3>
            <p className="text-sm text-gray-600">@{user.username}</p>
          </div>
        </div>
        
        <div className="space-y-2 mb-4">
          <div className="flex items-center text-sm text-gray-600">
            üìß {user.email}
          </div>
          <div className="flex items-center text-sm text-gray-600">
            üìû {user.phone}
          </div>
          <div className="flex items-center text-sm text-gray-600">
            üè¢ {user.company.name}
          </div>
        </div>

        <Link 
          to={`/user/${user.id}`}
          className="inline-block w-full text-center bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition-colors duration-200 font-medium"
        >
          View Details
        </Link>
      </div>
    </div>
  );
};

export default UserCard;
