import React from "react";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";

const UserCard = ({ user }) => {
  return (
    <motion.div
      className="relative backdrop-blur-md bg-white/30 rounded-xl shadow-md p-6 border border-white/40 cursor-pointer transition-all duration-500"
      whileHover={{ scale: 1.07, y: -8 }}   // lift + slight zoom
      whileTap={{ scale: 0.97 }}
      initial={{ opacity: 0, y: 40 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.4, ease: "easeOut" }}
    >
      {/* Glow Effect on Hover */}
      <div className="absolute inset-0 rounded-xl -z-10 opacity-0 group-hover:opacity-100 blur-2xl transition duration-500 bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500"></div>

      {/* Avatar + Name */}
      <div className="flex items-center mb-4">
        <div className="w-14 h-14 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full flex items-center justify-center text-white font-bold text-lg shadow-lg">
          {user.name.charAt(0).toUpperCase()}
        </div>
        <div className="ml-4">
          <h3 className="text-xl font-semibold text-gray-800">{user.name}</h3>
          <p className="text-sm text-gray-600">@{user.username}</p>
        </div>
      </div>

      {/* Details */}
      <div className="space-y-3 mb-5 text-gray-700">
        <div className="flex items-center text-sm">
          üìß {user.email}
        </div>
        <div className="flex items-center text-sm">
          üìû {user.phone}
        </div>
        <div className="flex items-center text-sm">
          üè¢ {user.company.name}
        </div>
      </div>

      {/* Button */}
      <motion.div whileHover={{ scale: 1.05 }}>
        <Link
          to={`/user/${user.id}`}
          className="inline-block w-full text-center bg-gradient-to-r from-blue-600 to-purple-600 text-white py-2 px-4 rounded-md hover:from-blue-700 hover:to-purple-700 transition-colors duration-300 font-medium shadow-md"
        >
          View Details
        </Link>
      </motion.div>
    </motion.div>
  );
};

export default UserCard;
